import React from "react";

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      days: 0,
      hours: 0,
      minutes: 0,
      seconds: 0,
    };
  }
  componentDidMount() {
    this.myInterval = setInterval(() => {
      const countDownDate = new Date("May 5, 2020 15:00:00").getTime();

      const now = new Date().getTime();
      const t = Date.parse(countDownDate) - Date.parse(now);
      var second = Math.floor((t / 1000) % 60);
      var minute = Math.floor((t / 1000 / 60) % 60);
      var hour = Math.floor((t / (1000 * 60 * 60)) % 24);
      //  const count = `   ${days} + "d " + ${hours} + "h "  + ${minutes}+ "m " + ${seconds} + "s "`;
      this.setState(({ days, hours, minutes, seconds }) => ({
        days: 0,
        hours: hour,
        minutes: minute,
        seconds: second,
      }));
    }, 1000);
  }

  render() {
    const { days, hours, minutes, seconds } = this.state;
    console.log(this.state);

    return (
      <h1>
        Time Remaining {days} :{hours}:{minutes} :{seconds}
      </h1>
    );
  }
}
export default Counter;
